svg.script:
// SVG namespace
let _SVG_NS = "http://www.w3.org/2000/svg";

const BG = document.getElementById("BG");
const BGbb = BG.getBoundingClientRect();

const WIDTH = BGbb.width;
const HEIGHT = BGbb.height;

const STARS = document.getElementById("stars");

//
for(var i = 0; i < 10; i++) {
  let circle = document.createElementNS(_SVG_NS, "circle");

  circle.setAttribute("cx", Math.random()*WIDTH);
  circle.setAttribute("cy", Math.random()*HEIGHT);
  circle.setAttribute("r", Math.random() + 1);
  circle.setAttribute("fill", "white");

  STARS.appendChild(circle);
}
//
